'use client';

import { context, renderQueryString } from '../chunk-OMREKXUP.js';
import { createElement } from 'react';

function NuqsTestingAdapter(props) {
  const useAdapter = () => ({
    searchParams: new URLSearchParams(props.searchParams),
    updateUrl(search, options) {
      props.onUrlUpdate?.({
        searchParams: search,
        queryString: renderQueryString(search),
        options
      });
    },
    rateLimitFactor: props.rateLimitFactor ?? 0
  });
  return createElement(
    context.Provider,
    { value: { useAdapter } },
    props.children
  );
}

export { NuqsTestingAdapter };
